#!/bin/bash
mysql -P 3310 -h 127.0.0.1 -u genny --password=password gennydb -e "select distinct (ea.baseEntityCode),ez1.valueString,ez0.valueString  from baseentity be,baseentity_attribute ea , baseentity_attribute e0 , baseentity_attribute ez0 , baseentity_attribute ez1 , baseentity_attribute ez3 , baseentity_attribute ez4 , baseentity_attribute ez5  where be.code=ea.baseentitycode and  ( ea.baseEntityCode like 'PER_%'  )   and ea.baseEntityCode=e0.baseEntityCode  and e0.attributeCode = 'PRI_IS_INTERN' and e0.valueBoolean = true and ea.baseEntityCode=ez0.baseEntityCode and ez0.attributeCode='PRI_NUM_JOURNALS'  and ea.baseEntityCode=ez1.baseEntityCode and ez1.attributeCode='PRI_STATUS'  and ea.baseEntityCode=ez3.baseEntityCode and ez3.attributeCode='PRI_START_DATE'  and ea.baseEntityCode=ez4.baseEntityCode and ez4.attributeCode='PRI_ASSOC_EP'  and ea.baseEntityCode=ez5.baseEntityCode and ez5.attributeCode='PRI_ADDRESS_STATE'  order by ez0.valueInteger ASC, ez1.valueString DESC, be.name ASC, ez3.valueDate ASC, ez4.valueString ASC, ez5.valueString ASC LIMIT 20 OFFSET 20" ;
